import ArgumentParser

struct Year2015Day1: ParsableCommand {
  static let configuration = CommandConfiguration(
    commandName: "1",
    abstract: "Day 1 Challenge",
    shouldDisplay: true,
    aliases: ["Day1"]
  )
  
  @OptionGroup var input: InputFileArgument

  mutating func run() throws {
    let input = try self.input.getInput() ?? Self.defaultInput

    var floor = 0
    for direction in Array(input) {
      switch direction {
      case "(": floor += 1
      case ")": floor -= 1
      default: ()
      }
    }

    print("Part 1 - Floor: \(floor)")

    var position = 0
    floor = 0
    for direction in Array(input) {
      switch direction {
      case "(": floor += 1
      case ")": floor -= 1
      default: ()
      }
      position += 1
      if floor == -1 { break }
    }

    print("Part 2 - Position: \(position)")
  }
  
