import ArgumentParser

struct Year2015Day3: ParsableCommand {
  static let configuration = CommandConfiguration(
    commandName: "3",
    abstract: "Day 3 Challenge",
    shouldDisplay: true,
    aliases: ["Day3"]
  )
  
  @OptionGroup var input: InputFileArgument

  mutating func run() throws {
    let input = try self.input.getInput() ?? Self.defaultInput
    
    var map: [[Int]] = [[1]]
    var position: (Int, Int) = (0, 0) // (y, x)
    var origin: (Int, Int) = (0, 0) // (y, x)

    for char in input {
      guard let direction = Direction(rawValue: char) else { continue }
      switch direction {
      case .north:
        moveNorth(from: &position, in: &map, origin: &origin)
      case .east:
        moveEast(from: &position, in: &map, origin: &origin)
      case .south:
        moveSouth(from: &position, in: &map, origin: &origin)
      case .west:
        moveWest(from: &position, in: &map, origin: &origin)
      }
      let (y, x) = mapCoordinates(for: position, with: origin)
      map[y][x] += 1
    }

    var houses = 0
    for row in map {
      for house in row {
        if house > 0 { houses += 1 }
      }
    }

    print("Part 1 - Houses: \(houses)")

    var map2: [[Int]] = [[2]]
    var origin2: (Int, Int) = (0, 0)
    var santaPos: (Int, Int) = (0, 0) // (y, x)
    var robotPos: (Int, Int) = (0, 0)

    var count = 0

    for char in input {
      guard let direction = Direction(rawValue: char) else { continue }
      switch direction {
      case .north:
        if count % 2 == 0 {
          moveNorth(from: &santaPos, in: &map2, origin: &origin2)
        } else {
          moveNorth(from: &robotPos, in: &map2, origin: &origin2)
        }
      case .east:
        if count % 2 == 0 {
          moveEast(from: &santaPos, in: &map2, origin: &origin2)
        } else {
          moveEast(from: &robotPos, in: &map2, origin: &origin2)
        }
      case .south:
        if count % 2 == 0 {
          moveSouth(from: &santaPos, in: &map2, origin: &origin2)
        } else {
          moveSouth(from: &robotPos, in: &map2, origin: &origin2)
        }
      case .west:
        if count % 2 == 0 {
          moveWest(from: &santaPos, in: &map2, origin: &origin2)
        } else {
          moveWest(from: &robotPos, in: &map2, origin: &origin2)
        }
      }
      
      if count % 2 == 0 {
        let (y, x) = mapCoordinates(for: santaPos, with: origin2)
        map2[y][x] += 1
      } else {
        let (y, x) = mapCoordinates(for: robotPos, with: origin2)
        map2[y][x] += 1
      }

      count += 1
    }

    var houses2 = 0
    for row in map2 {
      for house in row {
        if house > 0 { houses2 += 1 }
      }
    }

    print("Part 2 - Houses: \(houses2)")
  }

  // MARK: - Models
  enum Direction: Character {
    case north = "^"
    case east = ">"
    case south = "v"
    case west = "<"
  }

  func printMap(_ map: [[Int]]) {
    for row in map {
      print(row.map { String($0) }.joined(separator: " "))
    }
  }

  func mapCoordinates(
    for position: (Int, Int),
    with origin: (Int, Int)
  ) -> (Int, Int) {
    (position.0 + origin.0, position.1 + origin.1)
  }

  func moveNorth(
    from position: inout (Int, Int),
    in map: inout [[Int]],
    origin: inout (Int, Int)
  ) {
    position.0 -= 1
    let coordinates = mapCoordinates(for: position, with: origin)
    if coordinates.0 < 0 {
      map.insert(Array(repeating: 0, count: map[0].count), at: 0)
      origin.0 += 1
    }
  }

  func moveSouth(
    from position: inout (Int, Int),
    in map: inout [[Int]],
    origin: inout (Int, Int)
  ) {
    position.0 += 1
    let coordinates = mapCoordinates(for: position, with: origin)
    if coordinates.0 > map.count - 1 {
      map.append(Array(repeating: 0, count: map[0].count))
    }
  }

  func moveEast(
    from position: inout (Int, Int),
    in map: inout [[Int]],
    origin: inout (Int, Int)
  ) {
    position.1 += 1
    let coordinates = mapCoordinates(for: position, with: origin)
    if coordinates.1 > map[0].count - 1 {
      for i in map.indices {
        map[i].append(0)
      }
    }
  }

  func moveWest(
    from position: inout (Int, Int),
    in map: inout [[Int]],
    origin: inout (Int, Int)
  ) {
    position.1 -= 1
    let coordinates = mapCoordinates(for: position, with: origin)
    if coordinates.1 < 0 {
      for i in map.indices {
        map[i].insert(0, at: 0)
      }
      origin.1 += 1
    }
  }

